def.body:
if mini_is_valid?(m)
  private_key = Hash.sha256_string(m)
  return BigInt.new(private_key, 16)
else
  raise("mini private key is not valid (invalid checksum for: #{m})")
end
return BigInt.new("-999")

case pub.size
when 130, 128
  return decode_uncompressed_public_key(pub)
when 66
  return decode_compressed_public_key(pub, prime)
else
  raise("unknown public key format (invalid key size: #{pub.size})")
end
i = BigInt.new(-999)
return EC_Point.new(i, i)
